function initializeTimer(){var e=(new Date(2018,1,23,20,0)-new Date)/1e3;if(e>0){var t=e/60,o=t/60,n=o/24;t=60*(o-Math.floor(o)),o=Math.floor(o),e=Math.floor(60*(t-Math.floor(t))),t=Math.floor(t),n=Math.floor(n),setTimePage(o,t,e,n),timerId=setInterval(function(){0==e?0==t?0==o?0==n||(n--,o=24,t=59,e=59):(o--,t=59,e=59):(t--,e=59):e--,setTimePage(o,t,e=e<10?"0"+e:e,n)},1e3)}}function setTimePage(e,t,o,n){t=t<10?"0"+t:t,e=e<10?"0"+e:e,n=n<10?"0"+n:n;document.getElementById("timer");document.querySelector(".days").textContent=n,document.querySelector(".hours").textContent=e,document.querySelector(".minutes").textContent=t,document.querySelector(".sec").textContent=o,document.querySelector(".days-02").textContent=n,document.querySelector(".hours-02").textContent=e,document.querySelector(".minutes-02").textContent=t,document.querySelector(".sec-02").textContent=o,document.querySelector(".days-03").textContent=n,document.querySelector(".hours-03").textContent=e,document.querySelector(".minutes-03").textContent=t,document.querySelector(".sec-03").textContent=o}window.onload=function(){initializeTimer()};